import { defineStore } from 'pinia';
import { itemList } from '../data/itemList';

export const useWarehouseStore = defineStore('warehouse', {
  state: () => ({
    capacity: 20,
    coin:0,
    slayerCoin:0,
    items: new Map(),
  }),
  actions: {

    addItem(itemId, count) {
      const itemDetails = itemList[itemId];
      if (!itemDetails) {
        console.error('Item not found in item list:', itemId);
        return;
      }



      const currentItem = this.items.get(itemId) 
      if(currentItem){
        currentItem.count += count;
      }else{
        const newitem = {count: 0, ...itemDetails};
        newitem.count += count;
        this.items.set(itemId, currentItem);
        console.log(`Added ${count} ${itemDetails.name}(s) to the warehouse.`);
        console.log(this.items);
      }

    },

    getItemNumber(itemId) {
      const thisItem = this.items.get(itemId);
      return thisItem ? thisItem.count : 0;
    },
  },
  actions: {
    isWarehouseFull() {
        return this.items.size >= this.capacity;
    },
    addItem(itemId, count) {
      const itemDetails = itemList[itemId];
      if (!itemDetails) {
        console.error('Item not found in item list:', itemId);
        return;
      };

      if (this.isWarehouseFull() && !this.items.has(itemId)) {
        console.log('Warehouse is full. Cannot add new item type.');
        return;
      };
      const currentItem = this.items.get(itemId) || { count: 0, ...itemDetails };
      currentItem.count += count;
      this.items.set(itemId, currentItem);
      console.log(`Added ${count} ${itemDetails.name}(s) to the warehouse.`);
      console.log(this.items);
    },

    getItemNumber(itemId) {
      const thisItem = this.items.get(itemId);
      return thisItem ? thisItem.count : 0;
    },
  },
});
