import { defineStore } from 'pinia';
import { itemList } from '../data/itemList';

export const useWarehouseStore = defineStore('warehouse', {
  state: () => ({
    capacity: 20,
    coin:0,
    slayerCoin:0,
    items: new Map(),
    currentNotification: null,
    notificationTimer: null,
  }),

  actions: {
    isWarehouseFull() {
        return this.items.size >= this.capacity;
    },
    // 显示通知
    showNotification(itemName, itemQuantity) {
      if (this.currentNotification && this.currentNotification.itemName === itemName) {
        // 如果是相同物品，更新数量并刷新计时器
        this.currentNotification.itemQuantity = itemQuantity;

          this.currentNotification.element.innerHTML = `${itemName}: ${itemQuantity}`;

        clearTimeout(this.notificationTimer);
        this.startNotificationTimer(); // 重新计时
      } else {
        // 创建新通知
        this.createNotification(itemName, itemQuantity);
      }
    },

    createNotification(itemName, itemQuantity) {
      
      // 创建通知元素
      const notification = document.createElement('div');
      notification.innerHTML = `${itemName}: ${itemQuantity}`;
      notification.style.position = 'fixed';
      notification.style.bottom = '50px';
      notification.style.left = '50%';
      // notification.style.transform = 'translateX(-50%)';
      notification.style.backgroundColor = 'rgba(0, 0, 0, 0.7)';
      notification.style.color = 'white';
      notification.style.padding = '10px';
      notification.style.borderRadius = '5px';
      // notification.style.transition = 'bottom 0.5s ease-in-out';
      document.body.appendChild(notification);

      // 移动到屏幕底部
      // setTimeout(() => {
      //   notification.style.bottom = '20px';
      // }, 50);

      // 保存当前通知
      this.currentNotification = {
        itemName,
        itemQuantity,
        element: notification, // 保存对应的 DOM 元素
      };

      // 开始计时器，2秒后隐藏
      this.startNotificationTimer(notification);
    },

    startNotificationTimer(notification) {
      this.notificationTimer = setTimeout(() => {
        // 隐藏并删除通知
        notification.style.bottom = '-50px';
        setTimeout(() => {
          document.body.removeChild(notification);
        });

        // 重置当前通知
        this.currentNotification = null;
      }, 2000); // 2秒后消失
    },
    
    addItem(itemId, count) {
      const itemDetails = itemList[itemId];
      if (!itemDetails) {
        console.error('Item not found in item list:', itemId);
        return;
      }

      if (this.isWarehouseFull() && !this.items.has(itemId)) {
        console.log('Warehouse is full. Cannot add new item type.');
        return;
      }
      const currentItem = this.items.get(itemId) 
      if(currentItem){
        currentItem.count += count;
      }else{
        const newitem = {count: 0, ...itemDetails};
        newitem.count += count;
        this.items.set(itemId, newitem);
        console.log(`Added ${count} ${itemDetails.name}(s) to the warehouse.`);
        console.log(this.items);
      }
      this.showNotification(itemDetails.name, this.items.get(itemId).count);
    },
    costItem(itemId, count){
        const targetItem = this.items.get(itemId);
        if(targetItem.count < count){
            console.log(`Action failed , the number of ${targetItem.name} is not enough`);
            return
        }
        targetItem.count -= count;
        if(targetItem.count === 0){
            this.items.delete(itemId); 
        }
    },
    getItemNumber(itemId) {
      const thisItem = this.items.get(itemId);
      return thisItem ? thisItem.count : 0;
    },
  },
});
