import { defineStore } from 'pinia';

export const useActiveStore = defineStore('activePage', {
  state: () => ({
    activeSkill: null, 
    activeData: [],
    skillData: {} 
  }),

  actions: {
    activateSkill(skillName, skillData) {
      if (this.activeSkill !== skillName && this.activeSkill !== null) {
        this.deactivateSkill(this.activeSkill); 
      }
      this.activeSkill = skillName;
      this.skillData = skillData;
      this.startTimer(skillName);
    },

    deactivateSkill(skillName) {
      if (this.skillTimers[skillName]) {
        clearInterval(this.skillTimers[skillName]);
        delete this.skillTimers[skillName];
      }
      this.skillData[skillName].isActive = false;
      this.activeSkill = null;
    },

  }
});
