import { defineStore } from 'pinia';

export const useSkillStore = defineStore('skills', {
  state: () => ({
    skills: {
        攻击: { level: 1, exp: 0 },
        力量: { level: 1, exp: 0 },
        防御: { level: 1, exp: 0 },
        生命值: { level: 1, exp: 0 },
        远程: { level: 1, exp: 0 },
        魔法: { level: 1, exp: 0 },
        祝祭: { level: 1, exp: 0 },
        屠杀者: { level: 1, exp: 0 },
        // 被动技能
        种地: { level: 1, exp: 0 },
        城镇: { level: 1, exp: 0 },
  
        // 非战斗技能
        伐木: { level: 1, exp: 0 },
        钓鱼: { level: 1, exp: 0 },
        生火: { level: 1, exp: 0 },
        烹饪: { level: 1, exp: 0 },
        采矿: { level: 1, exp: 0 },
        锻造: { level: 1, exp: 0 },
        扒窃: { level: 1, exp: 0 },
        弓匠: { level: 1, exp: 0 },
        制造: { level: 1, exp: 0 },
        符文锻造: { level: 1, exp: 0 },
        草药学: { level: 1, exp: 0 },
        敏捷训练: { level: 1, exp: 0 },
        召唤: { level: 1, exp: 0 },
        天文学: { level: 1, exp: 0 },
        转换魔法: { level: 1, exp: 0 },
    }
  }),

  actions: {

    addExperience(skillId, expToAdd) {
      const skill = this.skills[skillId] || { level: 1, exp: 0 };
      const maxExpForCurrentLevel = this.calculateMaxExp(skill.level);
      skill.exp += expToAdd;

      while (skill.exp >= maxExpForCurrentLevel && skill.level < 99) {
        skill.exp -= maxExpForCurrentLevel;
        skill.level++;
      }

      this.skills[skillId] = skill;
    },
  },
});
